insert into recommendations (id, description, type, alternatives, sentence_type) values (10,'Revisión de eventos críticos', 2, '"el cirujano revisa: los pasos críticos o imprevistos, la duración de la operación y la pérdida de sangre prevista#el equipo de anestesia revisa: si el paciente presenta algún problema específico#el equipo de enfermería revisa: si se ha confirmado la esterilidad (con resultados de los indicadores) y si existen dudas o problemas relacionados con el instrumental y los equipos', 3);
insert into recommendations (id, description, type, alternatives, sentence_type) values (11,'¿Se ha administrado profilaxis antibiótica en los últimos 60 minutos?', 2, 'si#no procede', 2);
